import { useReducer ,useEffect,useState} from "react"
import reducer ,{initState} from "./recuder"
import {listItem,setItem,addItem,detailItem,updateItem,deleteItem} from "./actions"
import axios from 'axios';
function Crud({resource, fields,lopOptions}) {
    const [state,dispatch] =useReducer(reducer,initState)
    const [formData, setFormData] = useState({});
    const {item,items} = state;
    useEffect(() => {
        list()
    },[]);
    

    const list = () =>{
        
        axios.get(`http://localhost:8080/api/${resource}`).then((response)=>{
            dispatch(listItem(response.data));
            console.log(response.data);
        })
        .catch((error) => {
            console.log(error);
          });
    }
    const handleChange = (e) => {
        setFormData({ ...formData, [e.target.name]: e.target.value });
      };

    //   const handleAdd = () => {
     
    //   };
    return (
      <div className="container">
        <form>
          {fields.map((field) => (
            <div key={field.name}>
              <label>{field.label}</label>
              {field.type === 'select' ? (
                <select
                  name={field.name}
                  value={formData[field.name] || ''}
                  onChange={handleChange}
                  className="form-control"
                >
                  <option value="">Select {field.label}</option>
                  {field.name === 'Lops' && lopOptions.map((option) => (
                    <option key={option.value} value={option.value}>{option.label}</option>
                  ))}
                </select>
              ) : (
                <input
                  type={field.type}
                  name={field.name}
                  value={formData[field.name] || ''}
                  onChange={handleChange}
                  placeholder={field.placeholder}
                  className="form-control"
                />
              )}
            </div>
          ))}
        </form>
  
        <table className="table">
          <thead>
            <tr>
              {fields.map((field) => (
                <th key={field.name}>{field.label}</th>
              ))}
            </tr>
          </thead>
          <tbody>
            {items.map((item) => (
              <tr key={item.id}>
                {fields.map((field) => (
                  <td key={field.name}>{field.name === 'lop' ? lopOptions.find(option => option.value === item[field.name.id]).label : item[field.name]}</td>
                ))}
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    );
  };
    

export default Crud
