import { useEffect, useState } from "react";
import Crud from "../crud/curd";
import axios from "axios";

function SinhVien() {
  const sinhVienFields = [
    {
      name: "maSinhVien",
      label: "ma",
      type: "text",
      placeholder: "ma",
    },
    {
      name: "tenSinhVien",
      label: "ten",
      type: "text",
      placeholder: "ten",
    },
    {
      
        name: 'Lops',
        label: 'Class',
        type: 'select',
        options: [],
      
    },
   
    
  ];

  const [lopOptions, setLopOptions] = useState([]);

  useEffect(() => {
    // Gọi API để lấy dữ liệu của bảng lớp
    axios.get('http://localhost:8080/api/lop/hien-thi')
      .then((response) => {
        // Lưu trữ dữ liệu vào biến lopOptions
        setLopOptions(response.data);
      })
      .catch((error) => {
        console.log(error);
      });
  }, []);
  return(
    <div >
        <h2>CRUD Sinh vien</h2>
        <Crud resource="sinh-vien/hien-thi" fields={sinhVienFields  }/>
    </div>
  )
}
export default SinhVien;
