import { useReducer ,useEffect,useState} from "react"
import reducer ,{initState} from "./recuder"
import {listItem,setItem,addItem,detailItem,updateItem,deleteItem} from "./actions"
import axios from 'axios';
function Crud({resource, fields}) {
    const [state,dispatch] =useReducer(reducer,initState)
    const [formData, setFormData] = useState({});

    useEffect(() => {
        listItem();
    }, []);
    const {item,items} = state;

    const listItem = () =>{
        
        axios.get(`http://localhost:8080/api/${resource}`).then((response)=>{
            dispatch(listItem(response.data));
        })
        .catch((error) => {
            console.log(error);
          });
    }
    const handleChange = (e) => {
        setFormData({ ...formData, [e.target.name]: e.target.value });
      };

    //   const handleAdd = () => {
     
    //   };

    return(
        <div>
        <button onClick={listItem}>Refresh Data</button>
  
        <form>
          {fields.map((field) => (
            <div key={field.name}>
              <label>{field.label}</label>
              <input
                type={field.type}
                name={field.name}
                value={formData[field.name] || ''}
                onChange={handleChange}
                placeholder={field.placeholder}
              />
            </div>
          ))}
  
          {/* <button type="button" onClick={handleAdd}>
            Add
          </button> */}
        </form>
  
        {/* Hiển thị dữ liệu */}
        
          <ul>
            {state.items.map((item) => (
              <li key={item.id}>
                {/* Hiển thị các trường dữ liệu */}
                {fields.map((field) => (
                  <span key={field.name}>
                    {field.label}: {item[field.name]}
                  </span>
                ))}
  
                {/* Các nút chỉnh sửa và xóa */}
                {/* <button onClick={() => handleEdit(item)}>Edit</button>
                <button onClick={() => handleDelete(item.id)}>Delete</button> */}
              </li>
            ))}
          </ul>
        
      </div>
    );
  };
    

export default Crud
