import { useReducer, useEffect, useState } from "react";
import reducer, { initState } from "./recuder";
import {
  listItem,
  setItem,
  addItem,
  detailItem,
  updateItem,
  deleteItem,
} from "./actions";
import axios from "axios";
function Crud({ resource, fields, options }) {
  const [state, dispatch] = useReducer(reducer, initState);
  const [formData, setFormData] = useState({});
  const { item, items } = state;
  useEffect(() => {
    list();
  }, []);
  console.log(options);

  const list = () => {
    axios
      .get(`http://localhost:8080/api/${resource}/hien-thi`)
      .then((response) => {
        dispatch(listItem(response.data));
        console.log(response.data);
        console.log(options);
      })
      .catch((error) => {
        console.log(error);
      });
  };
  const handleChange = (e) => {
   dispatch(setItem(e.targe))
  };


    const handleAdd = () => {
      axios
      .post(`http://localhost:8080/api/${resource}/add`,formData)
      .then((response) =>{
        dispatch(addItem(response.data))
      })
      .catch((error) => {
        console.log(error);
      });
    };
  return (
    <div className="container">
      <form>
        {fields.map((field) => (
          <div key={field.name}>
            <label>{field.label}</label>
            {field.type === "select" ? (
              <select
                name={field.name}
                value={formData[field.name] || ""}
                onChange={handleChange}
                className="form-control"
              >
                {field.name === "idLop" &&
                  options.map((option) => (
                    <option key={option.id} value={option.id}>
                      {option.tenLop}
                    </option>
                  ))}
              </select>
            ) : (
              <input
                type={field.type}
                name={field.name}
                value={formData[field.name] || ""}
                onChange={handleChange}
                placeholder={field.placeholder}
                className="form-control"
              />
            )}{" "}
            <br />
          </div>
        ))}
      </form>
      <button onClick={handleAdd}>Add</button> {/* NÃºt Add */}
      <br /> <br /> <br />
      <table className="table">
        <thead>
          <tr>
            {fields.map((field) => (
              <th key={field.name}>{field.label}</th>
            ))}

            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {items.map((item) => (
            <tr key={item.id}>
              {fields.map((field) => (
                <td key={field.name}>
                  {field.name === 'idLop' ? item.lop.tenLop : item[field.name]}
                </td>
              ))}
              {/* <button onClick={() => handleDelete(item.id)}>Delete</button>{" "}
             
              <button onClick={() => handleUpdate(item.id)}>Update</button>{" "}
              */}
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default Crud;
