import { useReducer, useEffect, useState } from "react";
import reducer, { initState } from "./recuder";
import {
  listItem,
  setItem,
  addItem,
  detailItem,
  updateItem,
  deleteItem,
} from "./actions";
import axios from "axios";
function Crud({ resource, fields, options }) {
  const [state, dispatch] = useReducer(reducer, initState);
  const [formData, setFormData] = useState({});
  const { item, items } = state;
  useEffect(() => {
    list();
   
  }, []);
  console.log(options);

  const list = () => {
    axios
      .get(`http://localhost:8080/api/${resource}/hien-thi`)
      .then((response) => {
        dispatch(listItem(response.data));
        console.log(response.data);
        console.log(options);
      })
      .catch((error) => {
        console.log(error);
      });
  };
  const handleChange = (e) => {
      setFormData({ ...formData, [e.target.name]: e.target.value });
      
  };


    const handleAdd = () => {
      console.log(formData);
      
      axios
      .post(`http://localhost:8080/api/${resource}/add`,formData)
      .then((response) =>{
        dispatch(addItem(response.data))
        list()
        console.log(response.data);
        setFormData({})
      
      })
      .catch((error) => {
        console.log(error);
      });
    };

    const handleDelete  =(id) =>{
      axios
      .delete(`http://localhost:8080/api/${resource}/delete/${id}`)
      .then((response) =>{
        dispatch(deleteItem(response.data))
        list()
      })
      .catch((error) => {
        console.log(error);
      });
    }
  return (
    <div className="container">
    
      <button onClick={handleAdd}>Add</button> 
      <br /> <br /> <br />
      <table className="table">
        <thead>
          <tr>
            {fields.map((field) => (
              <th key={field.name}>{field.label}</th>
            ))}

            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {items.map((item) => (
            <tr key={item.id}>
              {fields.map((field) => (
                <td key={field.name}>
                  {field.name === 'idLop' ? item.lop.tenLop : item[field.name]}
                </td>
              ))}
              <button onClick={() => handleDelete(item.id)}>Delete</button>{" "}
             
              {/* <button onClick={() => handleUpdate(item.id)}>Update</button>{" "} */}
             
            </tr>
          ))}
        </tbody>
      </table>

      <div class="modal" tabindex="-1">
  <div class="modal-dialog">
    <div class="modal-content">
      <div class="modal-header">
        <h5 class="modal-title">Modal title</h5>
        <button type="button" class="btn-close" data-bs-dismiss="modal" aria-label="Close"></button>
      </div>
      <div class="modal-body">
      <form>
  {fields.map((field) => (
    <div key={field.name}>
      <label>{field.label}</label>
      {field.type === "select" ? (
        <select
          name={field.name}
          value={formData[field.name] || undefined}
          onChange={handleChange}
          className="form-control"
        >
          {field.options.map((option) => (
            <option key={option.id} value={option.id}>
              {option.tenLop}
            </option>
          ))}
        </select>
      ) : (
        <input
          type={field.type}
          name={field.name}
          value={formData[field.name] || ""}
          onChange={handleChange}
          placeholder={field.placeholder}
          className="form-control"
        />
      )}
      <br />
    </div>
  ))}
</form>
      </div>
      <div class="modal-footer">
      <button onClick={handleAdd}>Add</button> 
        <button type="button" class="btn btn-secondary" data-bs-dismiss="modal">Close</button>
        <button type="button" class="btn btn-primary" >Save</button>
      </div>
    </div>
  </div>
</div>


    </div>
  );
}

export default Crud;
