import { useReducer ,useEffect,useState} from "react"
import reducer ,{initState} from "./recuder"
import {listItem,setItem,addItem,detailItem,updateItem,deleteItem} from "./actions"
import axios from 'axios';
function Crud({resource, fields}) {
    const [state,dispatch] =useReducer(reducer,initState)
    const [formData, setFormData] = useState({});
    const {item,items} = state;
    useEffect(() => {
        list();
    },[]);
    

    const list = () =>{
        
        axios.get(`http://localhost:8080/api/${resource}`).then((response)=>{
            dispatch(listItem(response.data));
            console.log(response.data);
        })
        .catch((error) => {
            console.log(error);
          });
    }
    const handleChange = (e) => {
        setFormData({ ...formData, [e.target.name]: e.target.value });
      };

    //   const handleAdd = () => {
     
    //   };

    return(
        <div className="container">
  
        <form>
          {fields.map((field) => (
            <div key={field.name}>
              <label>{field.label}</label>
              <input
                type={field.type}
                name={field.name}
                value={formData[field.name] || ''}
                onChange={handleChange}
                placeholder={field.placeholder}
              />
            </div>
          ))}
  
          {/* <button type="button" onClick={handleAdd}>
            Add
          </button> */}
        </form>
       
    <table class="table">
  <thead>
        <tr>
          {fields.map((field) => (
            <th key={field.name}>{field.label}</th>
          ))}
        </tr>
  </thead>
  <tbody>
    {items.map((item) => (
          <tr key={item.id}>
            {fields.map((field) => (
              <td key={field.name}>{item[field.name]}</td>
            ))}
          </tr>
        ))}
  
  </tbody>
</table>
        
      </div>
    );
  };
    

export default Crud
