import { useEffect, useState } from "react";
import Crud from "../crud/curd";
import axios from "axios";

function SinhVien() {
  
  const [lopOptions, setLopOptions] = useState([]);
  const sinhVienFields = [
    {
      name: "maSinhVien",
      label: "ma",
      type: "text",
      placeholder: "ma",
    },
    {
      name: "tenSinhVien",
      label: "ten",
      type: "text",
      placeholder: "ten",
    },
    {
        name: 'lop',
        label: 'lop',
        type: 'select',
        options: lopOptions,
      
    },
   
    
  ];

  
 


 
  return(
    <div >
        <h2>CRUD Sinh vien</h2>
        <Crud resource="sinh-vien/hien-thi" fields={sinhVienFields} lopOptions ={lopOptions}/>
    </div>
  )
}
export default SinhVien;
